mapper.select:
SELECT id, name, price, overview, created_at, updated_at
FROM item
WHERE id BETWEEN #{min} AND #{max}

SELECT id, name, price, overview, created_at, updated_at
FROM item
WHERE id = #{id}

mapper.update:
UPDATE item
SET name = #{name}, price = #{price}, overview = #{overview}, updated_at
= #{updatedAt}
WHERE id = #{id}
