mapper.select:
SELECT id, name, price, overview, created_at, updated_at
FROM items
WHERE id BETWEEN #{min} AND #{max}

SELECT id, name, price, overview, created_at, updated_at
FROM items
WHERE id = #{id}

mapper.update:
UPDATE items
SET name = #{name}, price = #{price}, overview = #{overview}, updated_at = #{updatedAt}
WHERE id = #{id}
